document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelector(".burger-menu"),t=document.querySelector(".sidebar"),c=document.querySelector(".close-menu"),s=()=>{t.classList.toggle("open")};e.addEventListener("click",s),c.addEventListener("click",s),e.addEventListener("click",(function(){t.classList.add("active"),document.body.classList.add("sidebar-active")})),c.addEventListener("click",(function(){t.classList.remove("active"),document.body.classList.remove("sidebar-active")})),document.addEventListener("click",(c=>{t.contains(c.target)||e.contains(c.target)||!t.classList.contains("active")||(t.classList.remove("active"),t.classList.remove("open"),document.body.classList.remove("sidebar-active"))}))}));